			// Address width    = 7
			// Address LSBs     = 2
			{ 7'h60 }, //      cfg: 0x180
			{ 7'h50 }, //     gnet: 0x140
			{ 7'h40 }, // wb32_sio: 0x100
			{ 7'h38 }, //     sdio: 0x0e0
			{ 7'h30 }, //      fan: 0x0c0
			{ 7'h28 }, //     emmc: 0x0a0
			{ 7'h20 }, //   cpunet: 0x080
			{ 7'h18 }, //     uart: 0x060
