			.i_wb_data(wb32_sdio_data), // 32 bits wide
			.i_wb_sel(wb32_sdio_sel),  // 32/8 bits wide
		.o_wb_stall(wb32_sdio_stall),.o_wb_ack(wb32_sdio_ack), .o_wb_data(wb32_sdio_idata),
		.o_ck(o_sdio_clk),
		.i_ds(i_sdio_ds),
`ifdef	VERILATOR
		.io_cmd_tristate(io_sdio_cmd_tristate),
