		.o_int(sdio_int),
		.o_debug(sdio_debug)
		// }}}
	);

	// }}}
	// }}}
`else	// SDIO_ACCESS
	// {{{
	assign	o_sdio_clk = 1'b1;
`ifdef	VERILATOR
	assign	io_sdio_cmd_tristate = 1'b1;
	assign	o_sdio_cmd = 1'b1;
	assign	io_sdio_data_tristate  = -1;
	assign	o_sdio_data = -1;
`else	// VERILATOR
	assign	io_sdio_cmd  = 1'b1;
	assign	io_sdio_dat  = -1;
